// Process only the FIRST payment row that does not yet have a ReceiptID
// Skip all previous rows that already have receipts

const items = $input.all();
let output = [];

// Loop through all rows, find the first unprocessed one
for (const item of items) {
  const inItem = item.json;

  const StudentID = (inItem.StudentID || '').toString().trim();
  const Amount = Number(inItem.Amount || 0);
  const ParentEmail = (inItem.ParentEmail || '').toString().trim();
  const ReceiptID = (inItem.ReceiptID || inItem["Receipt Id"] || '').toString().trim();

  // Skip if this row already processed
  if (ReceiptID) continue;

  // Validate required fields
  if (!StudentID || Amount <= 0 || !ParentEmail) {
    console.log(`Invalid row skipped — StudentID:${StudentID}, Amount:${Amount}, ParentEmail:${ParentEmail}`);
    continue;
  }

  // Return only this first valid, unprocessed row
  output.push({
    json: {
      ...inItem,
      StudentID,
      Amount,
      ParentEmail,
      valid: true
    }
  });

  // Stop after first match
  break;
}

// If no new unprocessed rows found, return empty to stop workflow
if (output.length === 0) {
  console.log("No new unprocessed payment found (all have ReceiptID).");
  return [];
}

return output;

// Robust idempotency filter for receipts
// - Accepts single item, multiple items, or a single item whose .json is an array
// - Recognizes many ReceiptID key name variants
// - Logs counts for debugging
// - If something unexpected happens, falls back to returning original items (safe)

// helper: normalize incoming wrapper into array of items { json: {...} }
function normalizeInputs(rawInputs) {
  if (!rawInputs || rawInputs.length === 0) return [];
  // If n8n passed a single item whose json is an array of rows, expand it
  if (rawInputs.length === 1 && Array.isArray(rawInputs[0].json)) {
    return rawInputs[0].json.map(r => ({ json: r }));
  }
  // If some items have .json.data or .json.rows arrays, flatten them into items
  const expanded = [];
  for (const it of rawInputs) {
    const j = it.json;
    if (Array.isArray(j)) {
      j.forEach(r => expanded.push({ json: r }));
    } else if (Array.isArray(j && j.data)) {
      j.data.forEach(r => expanded.push({ json: r }));
    } else if (Array.isArray(j && j.rows)) {
      j.rows.forEach(r => expanded.push({ json: r }));
    } else {
      expanded.push(it);
    }
  }
  return expanded;
}

// helper: get ReceiptID value from a row with flexible key names
function getReceiptId(row) {
  if (!row || typeof row !== 'object') return '';
  const keys = ['ReceiptID','Receipt Id','receipt_id','receiptId','receipt','receiptID','receipt id'];
  for (const k of keys) {
    if (Object.prototype.hasOwnProperty.call(row, k)) {
      const v = row[k];
      if (v === null || v === undefined) return '';
      return String(v).trim();
    }
    // also try normalized lower-case keys
    const lowerKey = Object.keys(row).find(x => x.toLowerCase().replace(/\s+/g,'') === k.toLowerCase().replace(/\s+/g,''));
    if (lowerKey) {
      const v = row[lowerKey];
      if (v !== undefined && v !== null) return String(v).trim();
    }
  }
  return '';
}

try {
  let inputs = normalizeInputs($input.all());
  console.log('DEBUG: inputs length (after normalize):', inputs.length);

  // Filter items that do NOT have a ReceiptID (i.e., need processing)
  const toProcess = [];
  const skipped = [];
  for (const it of inputs) {
    const rid = getReceiptId(it.json);
    if (rid) skipped.push({ json: it.json, ReceiptID: rid });
    else toProcess.push(it);
  }

  console.log('DEBUG: toProcess:', toProcess.length, 'skipped:', skipped.length);

  // Return items needing processing (each as { json: ... })
  // If nothing to process, return empty array (workflow will skip downstream)
  return toProcess;

} catch (err) {
  // On unexpected error, log and return original inputs (fail-safe)
  console.error('ERROR in idempotency filter:', err);
  // Return original items instead of blocking the flow
  return $input.all().map(i => ({ json: i.json }));
}

