// Generate Receipt ID & PaymentDate (robust name/amount detection)
const item = $input.all()[0].json || {};
const now = new Date();
const y = now.getFullYear();
const suffix = String(Date.now()).slice(-6);
const receiptId = `R-${y}-${suffix}`;

// Try many common field names for Name and Amount, then sensible fallbacks
const resolvedName =
  item.Name ??
  item.name ??
  item.CustomerName ??
  item.customerName ??
  item.FullName ??
  item.fullName ??
  item.clientName ??
  item.ClientName ??
  item['Customer Name'] ??
  item['Full Name'] ??
  item.email ?? // use email if no name available
  "" ;

const resolvedAmount =
  item.Amount ??
  item.amount ??
  item.PaymentAmount ??
  item.paymentAmount ??
  item.total ??
  item.Total ??
  0;

// Build receipt data: include original item fields plus guaranteed fields
const receiptData = {
  ...item, // keep original data (so you don't lose any fields)
  ReceiptID: receiptId,
  Name: resolvedName,
  Amount: resolvedAmount,
  Status: 'Paid',
  PaymentDate: now.toISOString().slice(0, 10),
};

// Convert the JSON object into text and binary
const jsonString = JSON.stringify(receiptData, null, 2);
const dataBuffer = Buffer.from(jsonString, 'utf8');

return [
  {
    json: receiptData, // metadata available to downstream nodes
    binary: {
      data: {
        data: dataBuffer.toString('base64'),
        mimeType: 'application/json',
        fileName: `Receipt_${receiptId}.json`,
      },
    },
  },
];

